import React, {useState} from "react";

export default function Create(){
    const [val,setVal] = useState("");
   const changeInput = (e) => {
    setVal(e.target.value);
    props.onChange(val);
   };
    return (
    <div>
  <input type="text"
  name="Name"
  onChange={(e) => changeInput(e)}
  value={val}/>
            
  <input type="text"
  brand="Brand"
  onChange={(e) => changeInput(e)}
  value={val}/>
       
  <input type="submit"
  brand="Brand"
  onChange={(e) => changeInput(e)}
  value={val}/>
          
       </div>
)};
