import React, {useEffect} from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

export default function Detail(){
    const { dataId } = useParams();

    //useEffect is used to call the API
    useEffect(() => {
        axios.get("https://reqres.in/api/users?page=2/${dataId}").then((resp) => {
          console.log(resp.data);
        });
      }, [dataId]);
      

    return (
        <div className="container">
        <table className="table table-bordered table-striped text-center" border="1">
<thead>
          <tr>
            <th>Image</th>
           
          </tr>
        </thead>
        <tbody>
          {data.map((item) => (
            <tr key={item._id}>
              <td>{item.avatar}</td>
              <td>
                <button className="btn btn-primary" onClick={() => navigate(`/detail`)}>Details</button>
              </td>
            </tr>
          ))}
        </tbody> 
      </table> 
       </div>
    )
};
