import { useEffect, useState } from "react";
import "./App.css";
import axios from "axios";

function App() {
  const [product, setProduct] = useState([]);

  useEffect(() => {
    getList();
  }, []);

  const getList = () => {
    axios.get("http://localhost:8080/api/getAll").then((resp) => {
      console.log("resp is", resp.data);
      setProduct(resp.data);
    });
  };
  const deleteFunc = (id) => {
    axios.delete(`http://localhost:8080/api/delete/${id}`).then((resp) => {
      getList();
      alert(resp);
    });
    // updated list after deletion
  };

  return (
    <div className="row">
      <div className="col-md-12">
        <button className="btn btn-success float-right m-3">CREATE</button>
      </div>
      <table
        className="table table-bordered table-striped text-center"
        border="1"
      >
<thead>
          <tr>
            <th>Name</th>
            <th>Brand</th>
            <th colSpan={2}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {product.map((item) => (
            <tr key={item._id}>
              <td>{item.name}</td>
              <td>{item.brand}</td>
              <td>
                <button className="btn btn-primary">Edit</button>
              </td>
              <td>
                <button
                  className="btn btn-secondary"
                  onClick={() => deleteFunc(item._id)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
