import React, {useEffect, useState} from "react";
import axios from "axios";
import { useParams0  } from "react-router-dom";

export default function Detail(){
    const [data, setData] = useState([]);
  
    const { dataId } = useParams();

    //useEffect is used to call the API
    useEffect(() => {
        axios.get(`https://reqres.in/api/users?page=2/${dataId}`).then((resp) => {
          console.log(resp.data);
          setData(resp.data.avatar);
        });
      }, [dataId]);
      

    return (
        <div className="container">
        <table className="table table-bordered table-striped text-center" border="1">
<thead>
          <tr>
            <th>Image</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item) => (
            <tr key={item._id}>
              <td>
                <img alt="img not found"/>     
            </td>
            </tr>
          ))}
        </tbody> 
      </table> 
       </div>
    )
};
